uint32_t Length;
const uint8_t *Soundpt;

void Sound_Init(void){
	Length =0;
	DAC_Init();
	Timer0_Init(80000000/11000,4);
	
// write this
    // initialize a 11kHz Timer0A, and the DAC
};
void Timer0A_Handler(void){ // called at 11 kHz
	if (Length ){
		DAC_Out(*Soundpt);
		Soundpt++;
		Length--;
		
	}
	else{
		NVIC_DIS0_R = 1<<19;
		
	}
  TIMER0_ICR_R = 0x01;   // acknowledge TIMER0A timeout
  // output one value to DAC if a sound is active
}

//******* Sound_Start ************
// This function does not output to the DAC. 
// Rather, it sets a pointer and counter, and then enables the timer interrupt.
// It starts the sound, and the timer ISR does the output
// feel free to change the parameters
// Input: pt is a pointer to an array of DAC outputs
//        count is the length of the array
// Output: none
// special cases: as you wish to implement
void Sound_Start(const uint8_t *pt, uint32_t count){
	Length = count;
	Soundpt= pt;
	NVIC_EN0_R = 1<<19;
	
// write this
};
void Sound_Shoot(void){
	Sound_Start(pop, 3184);
// write this
};
void Sound_Jump(void){
	Sound_Start(bounce, 4672);
// write this
};
